dates = ['05-01-22', '04-29-22', '04-15-22', '04-05-22']

locations = [ {'address':"1540 W El Camino Ave, Sacramento, CA 95833", 'lat':38.61181759999999, 'lng':-121.4964048}, {'address':"5309 Sunrise Blvd, Fair Oaks, CA 95628", 'lat':38.6620478, 'lng':-121.2738131}, {'address':"1117 Roseville Square, Roseville, CA 95678", 'lat':38.7462886, 'lng':-121.274886}, {'address':"850 E Bidwell St, Folsom, CA 95630", 'lat':38.6724426, 'lng':-121.1559149,} ]

def add_location(list_pets):
    new_list_pets = []
    for pet in list_pets:
        location=choice(locations)
        pet["location"]=location['address']        
        pet['lat'] = location['lat']
        pet['lng'] = location['lng']
        pet['date']=choice(dates)
        new_list_pets.append(pet)
    return new_list_pets

